import{_ as n,c as a,o as e,ag as i}from"./chunks/framework.DPDPlp3K.js";const v=JSON.parse('{"title":"Koření","description":"","frontmatter":{},"headers":[],"relativePath":"suroviny/koreni.md","filePath":"suroviny/koreni.md"}'),l={name:"suroviny/koreni.md"};function r(t,o,s,k,u,d){return e(),a("div",null,o[0]||(o[0]=[i('<h1 id="koreni" tabindex="-1"><strong>Koření</strong> <a class="header-anchor" href="#koreni" aria-label="Permalink to &quot;**Koření**&quot;">​</a></h1><br><a href="/pdf/suroviny/koreni.pdf" download> 📝 Stáhnout PDF </a><h2 id="charakteristika" tabindex="-1"><strong>Charakteristika</strong> <a class="header-anchor" href="#charakteristika" aria-label="Permalink to &quot;**Charakteristika**&quot;">​</a></h2><p>Koření jsou čerstvé nebo sušené části tropických i domácích rostlin. Jejich charakteristickou chuť a vůni určují silice, oleje a alkaloidy. Výživová hodnota koření je zanedbatelná, využívá se především pro chuťové vlastnosti a podporu trávení díky dráždivým účinkům.</p><h2 id="deleni-koreni" tabindex="-1"><strong>Dělení koření</strong> <a class="header-anchor" href="#deleni-koreni" aria-label="Permalink to &quot;**Dělení koření**&quot;">​</a></h2><h3 id="podle-slozeni" tabindex="-1"><strong>Podle složení</strong> <a class="header-anchor" href="#podle-slozeni" aria-label="Permalink to &quot;**Podle složení**&quot;">​</a></h3><ul><li><strong>Jednodruhové</strong> – obsahuje pouze jednu rostlinu.</li><li><strong>Směsi koření</strong> – kombinace více druhů bez přidání dalších látek (např. perníkové koření).</li><li><strong>Kořenicí přípravky</strong> – obsahují kromě koření i sůl, zeleninu, houby, glutamát sodný apod.</li></ul><h3 id="z-hlediska-vyzivy" tabindex="-1"><strong>Z hlediska výživy</strong> <a class="header-anchor" href="#z-hlediska-vyzivy" aria-label="Permalink to &quot;**Z hlediska výživy**&quot;">​</a></h3><ul><li><strong>Prospěšné</strong> – např. čerstvé nebo sušené natě.</li><li><strong>Neškodné</strong> – neobsahují dráždivé látky, ale mají výrazné aroma (např. vanilka, skořice).</li><li><strong>Silně dráždivé</strong> – obsahují dráždivé alkaloidy (např. pepř, pálivá paprika).</li></ul><h3 id="podle-casti-rostliny" tabindex="-1"><strong>Podle části rostliny</strong> <a class="header-anchor" href="#podle-casti-rostliny" aria-label="Permalink to &quot;**Podle části rostliny**&quot;">​</a></h3><ul><li><p><strong>Z podzemních částí</strong>:</p><ul><li>zázvor (CV – zázvorky)</li><li>kurkuma</li><li>puškvorec</li></ul></li><li><p><strong>Z kůry</strong>:</p><ul><li>skořice – je to usušená kůra skořicovníku cejlonského (kvalitnější) nebo skořicovníku čínského. Používá se buď celá nebo mletá. Barva usušené kůry je žlutohnědá až červenohnědá. Má příjemnou vůni, nasládlou kořeněnou chuť. <ul><li>(CV – dezerty, koláče, sušenky, pečivo, nápoje, ochucování těst)</li></ul></li></ul></li><li><p><strong>Z listů a celých rostlin</strong>:</p><ul><li>bobkový list, majoránka</li><li>tymián (CV – výroba perníku)</li><li>citronela (CV – cukrářské výrobky)</li><li>máta (CV – moučníky, čokolády, likéry, saláty)</li><li>meduňka (CV – dorty s jogurtovou/tvarohovou polevou)</li><li>bazalka, šalvěj, estragon, rozmarýn, saturejka, oregáno</li></ul></li><li><p><strong>Z květů a květních součástí</strong>:</p><ul><li>hřebíček (CV – perníkové koření, aromatizace náplní a kompotů)</li><li>kapary</li><li>šafrán (CV – moučníky a těsta)</li></ul></li><li><p><strong>Z plodů a semen</strong>:</p><ul><li><p><strong>Vanilka</strong> – fermentované plody vanilovníku plocholistého.</p><ul><li><strong>Úprava za sucha (mexický způsob)</strong> – tobolky se nechávají zavadnout, pak se střídá slunění a pocení. To se opakuje několik týdnů. Fermentací se mění barva tobolek na hnědočernou, vanilin vykrystalizovává na povrchu a vanilka dostává charakteristickou vůni.</li><li><strong>Úprava mokrou metodou (oblasti Indického oceánu)</strong> – sklizené tobolky se ponoří na několik sekund do 80-100°C horké vody, poté následuje fermentace stejná jako u mexického způsobu. Doba fermentace je kratší (4-7 dní).</li><li>(CV – ochucování zmrzlin, náplní a krémů, výroba likérů)</li><li>V současnosti je vanilka nahrazována synteticky připraveným vanilínem.</li></ul></li><li><p><strong>Muškátový oříšek a květ</strong> (CV – náplně, těsta)</p></li><li><p><strong>Kmín</strong> (CV – slané tyčinky, pečivo)</p></li><li><p><strong>Nové koření, fenykl, anýz, badyán, kardamom</strong> (CV – perníkové koření)</p></li></ul></li></ul><p>V cukrářské výrobě (<strong>CV</strong>) se používá převážně mleté koření bez vad a škůdců.</p>',13)]))}const h=n(l,[["render",r]]);export{v as __pageData,h as default};
