import{_ as o,c as t,o as n,ag as l}from"./chunks/framework.DPDPlp3K.js";const h=JSON.parse('{"title":"Máslo","description":"","frontmatter":{"prev":{"text":"Suroviny","link":"/suroviny"}},"headers":[],"relativePath":"suroviny/maslo.md","filePath":"suroviny/maslo.md"}'),e={name:"suroviny/maslo.md"};function r(s,a,i,m,v,u){return n(),t("div",null,a[0]||(a[0]=[l('<h1 id="maslo" tabindex="-1">Máslo <a class="header-anchor" href="#maslo" aria-label="Permalink to &quot;Máslo&quot;">​</a></h1><br><a href="/pdf/suroviny/maslo.pdf" download> 📄 Stáhnout PDF </a><h2 id="charakteristika" tabindex="-1">Charakteristika <a class="header-anchor" href="#charakteristika" aria-label="Permalink to &quot;Charakteristika&quot;">​</a></h2><p>Máslo je mléčný tuk s emulgovanou vodní složkou, která obsahuje určité množství bílkovin, laktózy, minerálních látek a vitamínů (A, E, D).</p><h2 id="slozeni" tabindex="-1">Složení <a class="header-anchor" href="#slozeni" aria-label="Permalink to &quot;Složení&quot;">​</a></h2><ul><li>mléčný tuk</li><li>bílkoviny</li><li>mléčný cukr (laktóza)</li><li>kyselina mléčná</li><li>minerální látky</li><li>vitamíny (A, E, D)</li><li>voda</li></ul><h2 id="vyroba-masla" tabindex="-1">Výroba másla <a class="header-anchor" href="#vyroba-masla" aria-label="Permalink to &quot;Výroba másla&quot;">​</a></h2><p>Máslo se vyrábí z pasterované smetany o tučnosti 37–40 %.</p><h3 id="_1-zrani-smetany" tabindex="-1">1. Zrání smetany <a class="header-anchor" href="#_1-zrani-smetany" aria-label="Permalink to &quot;1. Zrání smetany&quot;">​</a></h3><ul><li><strong>Fyzikální zrání</strong> – zchlazení smetany na 8 °C, částečné vykrystalizování tuku.</li><li><strong>Biologické zrání</strong> – zaočkování smetany smetanovým zákysem (v mlékárnách se moc nepoužívá).</li></ul><h3 id="_2-vlastni-zpracovani" tabindex="-1">2. Vlastní zpracování <a class="header-anchor" href="#_2-vlastni-zpracovani" aria-label="Permalink to &quot;2. Vlastní zpracování&quot;">​</a></h3><p>Máslo se vyrábí <strong>zpevňovacím způsobem</strong>, kdy se smetana ve zmáselňovačce nejprve zpění a poté stlouká.</p><h4 id="zmaselnovac" tabindex="-1"><strong>Zmáselňovač</strong> <a class="header-anchor" href="#zmaselnovac" aria-label="Permalink to &quot;**Zmáselňovač**&quot;">​</a></h4><ul><li><strong>Stloukací válec</strong> – našlehává smetanu, v pěně dochází ke koncentraci kuliček tuku, které se spojují ve větší celky = máselné zrno.</li><li><strong>Odlučovač</strong> – dochází k dalšímu shlukování máselného zrna a oddělení od podmáslí.</li><li><strong>Hnětač</strong> – máselné zrno se hněte a spojuje v souvislou máselnou hmotu, zároveň se upravuje obsah vody v másle.</li></ul><p>Hotové máslo se formuje do hranolů o hmotnosti <strong>125 g nebo 250 g</strong> a balí do hliníkových fólií.</p><h2 id="vyznam-ve-vyzive" tabindex="-1">Význam ve výživě <a class="header-anchor" href="#vyznam-ve-vyzive" aria-label="Permalink to &quot;Význam ve výživě&quot;">​</a></h2><ul><li>Nejrozšířenější a nejpoužívanější živočišný tuk.</li><li>Snadno stravitelné.</li><li>Má příjemnou chuť, vůni a barvu.</li><li>Snadno se roztírá.</li><li>Nevýhodou je krátká trvanlivost.</li></ul><h2 id="vyznam-v-cukrarske-vyrobe" tabindex="-1">Význam v cukrářské výrobě <a class="header-anchor" href="#vyznam-v-cukrarske-vyrobe" aria-label="Permalink to &quot;Význam v cukrářské výrobě&quot;">​</a></h2><p>Máslo je klíčovou surovinou pro:</p><ul><li><strong>Krémy a náplně</strong></li><li><strong>Těsta, korpusy a pečené výrobky</strong> – slouží jako pojivo nebo obohacuje výrobky o tukový podíl.</li></ul><h2 id="skladovani" tabindex="-1">Skladování <a class="header-anchor" href="#skladovani" aria-label="Permalink to &quot;Skladování&quot;">​</a></h2><ul><li>Skladuje se v čistých, chlazených prostorách do <strong>6 °C</strong>.</li><li>Nesmí být skladováno se silně aromatickými surovinami (přijímá pachy).</li><li><strong>Čerstvé máslo</strong> – skladovatelnost <strong>20 dnů</strong> od výroby.</li><li><strong>Zmrazené máslo</strong> – skladovatelnost <strong>2 roky</strong>.</li></ul><h2 id="druhy-masla" tabindex="-1">Druhy másla <a class="header-anchor" href="#druhy-masla" aria-label="Permalink to &quot;Druhy másla&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Druh másla</th><th>Obsah tuku</th></tr></thead><tbody><tr><td><strong>Čerstvé a stolní máslo</strong></td><td>min. 80 %</td></tr><tr><td><strong>Máslo se smetanovým zákysem</strong></td><td>min. 75 %</td></tr><tr><td><strong>Máslo se sníženým obsahem tuku</strong></td><td>min. 60 %</td></tr><tr><td><strong>Máslo s nízkým obsahem tuku</strong></td><td>min. 39 %</td></tr><tr><td><strong>Pomazánkové</strong> <em>(nesmí se označovat jako máslo)</em></td><td>min. 31 %</td></tr></tbody></table>',25)]))}const k=o(e,[["render",r]]);export{h as __pageData,k as default};
