import{_ as o,c as r,o as t,ag as e}from"./chunks/framework.DPDPlp3K.js";const k=JSON.parse('{"title":"Tvaroh","description":"","frontmatter":{},"headers":[],"relativePath":"suroviny/tvaroh.md","filePath":"suroviny/tvaroh.md"}'),l={name:"suroviny/tvaroh.md"};function n(i,a,s,h,v,u){return t(),r("div",null,a[0]||(a[0]=[e('<h1 id="tvaroh" tabindex="-1">Tvaroh <a class="header-anchor" href="#tvaroh" aria-label="Permalink to &quot;Tvaroh&quot;">​</a></h1><br><a href="/pdf/suroviny/tvaroh.pdf" download> 📄 Stáhnout PDF </a><h2 id="charakteristika" tabindex="-1">Charakteristika <a class="header-anchor" href="#charakteristika" aria-label="Permalink to &quot;Charakteristika&quot;">​</a></h2><p>Tvaroh je mléčná bílkovina kasein, získaná ze sraženého mléka.<br> Kasein se nachází v mléce ve formě koloidního roztoku a sražením mléka se vyloučí v pevné formě (tvarohovina).</p><p>Tvaroh je bílá až krémová hmota, která může mít vláčnou nebo tvrdou konzistenci. Chuť je sladká nebo mírně mléčně nakyslá, bez cizích vůní a příchutí.</p><h2 id="druhy" tabindex="-1">Druhy <a class="header-anchor" href="#druhy" aria-label="Permalink to &quot;Druhy&quot;">​</a></h2><h3 id="podle-zpusobu-vyroby" tabindex="-1">Podle způsobu výroby <a class="header-anchor" href="#podle-zpusobu-vyroby" aria-label="Permalink to &quot;Podle způsobu výroby&quot;">​</a></h3><ul><li><strong>Měkký tvaroh</strong></li><li><strong>Tvrdý tvaroh</strong></li></ul><h3 id="podle-obsahu-tuku-v-susine" tabindex="-1">Podle obsahu tuku v sušině <a class="header-anchor" href="#podle-obsahu-tuku-v-susine" aria-label="Permalink to &quot;Podle obsahu tuku v sušině&quot;">​</a></h3><ul><li><strong>Tučný tvaroh</strong> (min. 38 % t. v s.)</li><li><strong>Polotučný tvaroh</strong> (min. 15 % t. v s.)</li><li><strong>Nízkotučný (jemný) tvaroh</strong> (min. 5 % t. v s.)</li><li><strong>Odtučněný tvaroh</strong> (méně než 5 % t. v s.)</li></ul><h3 id="dalsi-druhy-tvarohu" tabindex="-1">Další druhy tvarohu <a class="header-anchor" href="#dalsi-druhy-tvarohu" aria-label="Permalink to &quot;Další druhy tvarohu&quot;">​</a></h3><ul><li>Tvrdý tvaroh na strouhání</li><li>Tvaroh pro pekaře</li><li>Tvarohové krémy</li><li>Šlehaný tvaroh</li><li>Tvarohové dezerty (Termix, Bobík, Pribináček, Lipánek …)</li></ul><h2 id="vyroba" tabindex="-1">Výroba <a class="header-anchor" href="#vyroba" aria-label="Permalink to &quot;Výroba&quot;">​</a></h2><h3 id="tvrdy-tvaroh" tabindex="-1">Tvrdý tvaroh <a class="header-anchor" href="#tvrdy-tvaroh" aria-label="Permalink to &quot;Tvrdý tvaroh&quot;">​</a></h3><p>Vyrábí se z pasterovaného mléka zkysaného buď samovolně, nebo přidáním bakterií mléčného kvašení.</p><ol><li>Při <strong>teplotě 20°C</strong> se mléčný cukr mění na kyselinu mléčnou.</li><li>Po dosažení požadované kyselosti se mléko zahřeje na <strong>35°C</strong>.</li><li>Kyselina mléčná vysráží kasein na chuchvalce tvarohu.</li><li>Oddělí se syrovátka a tvaroh se lisuje na obsah sušiny <strong>32 %</strong>.</li><li>Balí se do spotřebitelských obalů.</li></ol><h3 id="mekky-tvaroh" tabindex="-1">Měkký tvaroh <a class="header-anchor" href="#mekky-tvaroh" aria-label="Permalink to &quot;Měkký tvaroh&quot;">​</a></h3><p>Vyrábí se z pasterovaného mléka s přídavkem smetanového zákysu a syřidla.</p><ul><li><strong>Syřidlo</strong> obsahuje enzym <strong>chymosin</strong>, který sráží mléčnou bílkovinu bez zkysání mléka.</li></ul><h4 id="vyrobni-metody" tabindex="-1">Výrobní metody: <a class="header-anchor" href="#vyrobni-metody" aria-label="Permalink to &quot;Výrobní metody:&quot;">​</a></h4><ul><li><strong>Klasická metoda</strong> – po vysrážení se tvarohovina vypouští do pytlů a lisuje se na sušinu <strong>25 %</strong>.</li><li><strong>Moderní metoda</strong> – syrovátka se odděluje filtrací nebo odstředěním na sušinu <strong>17–23 %</strong>.</li></ul><p>Poté se tvaroh vychladí, tvaruje a balí.</p><h2 id="pouziti-v-cukrarske-vyrobe" tabindex="-1">Použití v cukrářské výrobě <a class="header-anchor" href="#pouziti-v-cukrarske-vyrobe" aria-label="Permalink to &quot;Použití v cukrářské výrobě&quot;">​</a></h2><ul><li><strong>Tvrdý tvaroh</strong> – výroba moučníků (lívance, bavorské vdolečky …).</li><li><strong>Měkký tvaroh</strong> – tvarohové pomazánky, těsta, náplně do kynutých výrobků a tvarohových řezů.</li></ul><h2 id="vyzivova-hodnota" tabindex="-1">Výživová hodnota <a class="header-anchor" href="#vyzivova-hodnota" aria-label="Permalink to &quot;Výživová hodnota&quot;">​</a></h2><p>Tvaroh je bohatý na:</p><ul><li><p>Lehce stravitelné bílkoviny</p></li><li><p>Vápenaté soli</p></li><li><p>Fosforečné soli</p></li><li><p>Mléčný tuk</p></li></ul><p>Tyto živiny podporují:</p><ul><li><p>Růst</p></li><li><p>Nervovou soustavu</p></li></ul>',30)]))}const b=o(l,[["render",n]]);export{k as __pageData,b as default};
