import{_ as e,c as a,o as l,ag as i}from"./chunks/framework.DPDPlp3K.js";const c=JSON.parse('{"title":"Vejce","description":"","frontmatter":{},"headers":[],"relativePath":"suroviny/vejce.md","filePath":"suroviny/vejce.md"}'),t={name:"suroviny/vejce.md"};function n(r,o,s,u,d,v){return l(),a("div",null,o[0]||(o[0]=[i('<h1 id="vejce" tabindex="-1">Vejce <a class="header-anchor" href="#vejce" aria-label="Permalink to &quot;Vejce&quot;">​</a></h1><br><a href="/pdf/suroviny/vejce.pdf" download> 📄 Stáhnout PDF </a><h2 id="charakteristika" tabindex="-1">Charakteristika <a class="header-anchor" href="#charakteristika" aria-label="Permalink to &quot;Charakteristika&quot;">​</a></h2><p>Vejce je velká buňka se zárodkem budoucího živočicha a zásobou živin pro jeho vývoj. Zárodek i živiny chrání skořápka. V potravinářství se pojmem „vejce“ rozumí vždy vejce slepičí.</p><h2 id="slozeni" tabindex="-1">Složení <a class="header-anchor" href="#slozeni" aria-label="Permalink to &quot;Složení&quot;">​</a></h2><h3 id="bilek" tabindex="-1">Bílek <a class="header-anchor" href="#bilek" aria-label="Permalink to &quot;Bílek&quot;">​</a></h3><ul><li>Koloidní vodný roztok plnohodnotných bílkovin (ovoalbumin, ovoglobulin)</li><li>Minerální látky: vápník, fosfor, železo, síra, vitamín B₂</li><li>Čerstvý bílek: hustý, průhledný, čistý, bez skvrn</li><li>Stářím řídne</li><li>Má velkou šlehatelnost díky vaječné bílkovině mukoidu</li><li>Zásaditá reakce, mísitelný s vodou, teplem se sráží</li></ul><h3 id="zloutek" tabindex="-1">Žloutek <a class="header-anchor" href="#zloutek" aria-label="Permalink to &quot;Žloutek&quot;">​</a></h3><ul><li>Vodná tuková emulze s bílkovinami (ovolecitin – hlavní emulgátor při výrobě majonézy)</li><li>Minerální látky: vápník, fosfor, železo, síra</li><li>Vitamíny: A, D, E, K, skupiny B a PP</li><li>Čerstvý žloutek: pevný, kulatý, pravidelný, vysoký, čistý</li><li>Barva: žlutá až oranžová (závisí na potravě slepic) – tmavší obsahují více vitamínu A</li><li>Stářím řídne a stává se roztékavým</li></ul><h2 id="trideni" tabindex="-1">Třídění <a class="header-anchor" href="#trideni" aria-label="Permalink to &quot;Třídění&quot;">​</a></h2><p>Vejce se třídí podle jakosti, skořápky, vzduchové bubliny a hmotnosti.</p><h3 id="podle-jakosti" tabindex="-1">Podle jakosti <a class="header-anchor" href="#podle-jakosti" aria-label="Permalink to &quot;Podle jakosti&quot;">​</a></h3><ul><li><strong>Třída A</strong> – kvalitní vejce</li><li><strong>Třída B</strong> – může mít slabé znečištění nebo deformaci</li></ul><h3 id="podle-vzduchove-bubliny" tabindex="-1">Podle vzduchové bubliny <a class="header-anchor" href="#podle-vzduchove-bubliny" aria-label="Permalink to &quot;Podle vzduchové bubliny&quot;">​</a></h3><ul><li><strong>EXTRA A</strong> – max. 4 mm</li><li><strong>A</strong> – max. 6 mm</li><li><strong>B</strong> – max. 9 mm</li></ul><h3 id="podle-hmotnosti-pouze-pro-tridu-a" tabindex="-1">Podle hmotnosti (pouze pro třídu A) <a class="header-anchor" href="#podle-hmotnosti-pouze-pro-tridu-a" aria-label="Permalink to &quot;Podle hmotnosti (pouze pro třídu A)&quot;">​</a></h3><ul><li><strong>XL (velmi velká)</strong> – 73 g a více</li><li><strong>L (velká)</strong> – 63–73 g</li><li><strong>M (střední)</strong> – 53–63 g</li><li><strong>S (malá)</strong> – méně než 53 g</li></ul><h2 id="skladovani" tabindex="-1">Skladování <a class="header-anchor" href="#skladovani" aria-label="Permalink to &quot;Skladování&quot;">​</a></h2><ul><li>Krátkodobé uchovávání při stabilních podmínkách</li><li>Ukládají se špičkou dolů do čistých proložek</li><li>Skladování ve vzdušných, dobře větratelných skladech bez aromatických potravin</li><li>Nevystavovat přímému slunci ani tepelnému zdroji</li><li><strong>Čerstvě snesená</strong>: 10–15 °C, relativní vlhkost 70–80 %</li><li><strong>Po třídění</strong>: chlazení na 5–15 °C</li></ul><h2 id="pouziti" tabindex="-1">Použití <a class="header-anchor" href="#pouziti" aria-label="Permalink to &quot;Použití&quot;">​</a></h2><ul><li><strong>Cukrářství</strong> – pěnotvorné, emulgační a koagulační vlastnosti vajec</li><li><strong>Šlehané hmoty</strong> – kombinace vaječné pěny s cukrem, moukou a dalšími přísadami → pórovitost, dobrá stravitelnost</li><li><strong>Krémy, náplně, zmrzliny</strong> – využití šlehatelnosti a koagulace bílkovin</li><li><strong>Polevy</strong> – z celých vajec, bílků nebo žloutků</li><li><strong>Potírání pečiva</strong> – lesklý, zlatohnědý povrch</li><li><strong>Jádrové a speciální výrobky</strong> – bílky spojují suroviny do elastické hmoty nebo se šlehají s cukrem a zahřívají k zahuštění</li></ul><h2 id="hygiena-v-cukrarstvi" tabindex="-1">Hygiena v cukrářství <a class="header-anchor" href="#hygiena-v-cukrarstvi" aria-label="Permalink to &quot;Hygiena v cukrářství&quot;">​</a></h2><ul><li>Vejce s vadami jsou nepoužitelná</li><li>Důsledná hygiena při zpracování vajec, bílků a žloutků</li><li>Dodržování technologických a hygienických postupů při výrobě i expedici hotových produktů</li></ul>',24)]))}const k=e(t,[["render",n]]);export{c as __pageData,k as default};
